mapper.update:
update survey_paper set update_time = sysdate
,title = #{title}
,start_time = #{startTime}
,end_time = #{endTime}
,is_publish = #{isPublish}
,is_del = #{isDel}
where id = #{id}

mapper.select:
select id,title,start_time startTime,end_time endTime,is_publish isPublish,
create_time createTime,update_time updateTime
from survey_paper where 1=1
and title = #{title}
and is_publish = #{isPublish}
and is_del = #{isDel}

select id,title,start_time startTime,end_time endTime,is_publish isPublish,
create_time createTime,update_time updateTime
from survey_paper where id = #{id}
