mapper.update:
update survey_question set update_time = sysdate
,title = #{title}
,mode_type = #{modeType}
,options = #{options}
,is_del = #{isDel}
where id = #{id}

mapper.select:
select id,title,mode_type modeType,options,is_del isDel,create_time createTime,update_time updateTime
from survey_question where 1=1
 and title like CONCAT('%',#{title},'%')
 and id = #{id}
 and is_del = #{idDel}
order by update_time
